import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  fromCurrency: string = 'USD';
  toCurrency: string = 'USD';
  amount: number = 0;
  convertedAmount: number | null = null; // Initialize as null

  forexRates: { id: string, value: number }[] = [
    { "id": "USD", "value": 1.126735 },
    { "id": "GBP", "value": 0.876893 },
    { "id": "INR", "value": 79.677056 }
  ];

  convertForex() {
    const fromRate = this.forexRates.find(rate => rate.id === this.fromCurrency)?.value || 1;
    const toRate = this.forexRates.find(rate => rate.id === this.toCurrency)?.value || 1;

    this.convertedAmount = (this.amount / fromRate) * toRate;
  }
}
<div>
  <form #converterForm="ngForm" (ngSubmit)="convertForex()">
    <label for="fromCurrency">Source CCY:</label>
    <select id="fromCurrency" name="fromCurrency" [(ngModel)]="fromCurrency" required>
      <option value="USD">USD</option>
      <option value="GBP">GBP</option>
      <option value="INR">INR</option>
    </select>

    <label for="toCurrency">Target CCY:</label>
    <select id="toCurrency" name="toCurrency" [(ngModel)]="toCurrency" required>
      <option value="USD">USD</option>
      <option value="GBP">GBP</option>
      <option value="INR">INR</option>
    </select>

    <label for="amount">Amount(Rs):</label>
    <input type="number" id="amount" name="amount" [(ngModel)]="amount" required>

    <button type="submit" id="submitButton">Submit</button>
  </form>

  <div id="result">
    <span *ngIf="convertedAmount !== null">Converted Amount: {{ convertedAmount | number: '1.2-2' }}</span>
  </div>
</div>
